//services
const noSleep = new NoSleep();

//containers
const counterDiv = document.getElementById("counter");
const exerciseName = document.getElementById("exercise-name");
const container = document.getElementById("main-body");

//flags
let runningInterval = null;

clearInterval(runningInterval);

//constants
const RUNNING_TYPES = {
	ACTIVE: "ACTIVE",
	REST: "REST",
}

const RUNNING_TYPE_CONFIGURATIONS = {
	ACTIVE: {
		backgroundColor: "#66ff00",
		defaultTimeInSeconds: 3,
		nextType: RUNNING_TYPES.REST
	},
	REST: {
		backgroundColor: "#ff0000",
		defaultTimeInSeconds: 3,
		nextType: RUNNING_TYPES.ACTIVE
	},
}

const queryString = window.location.search;
const urlParams = new URLSearchParams(queryString);
const workoutJson = JSON.parse(atob(urlParams.get("workout")));

console.log(workoutJson);

//state
let configurationState = {
	[RUNNING_TYPES.ACTIVE]: {
		timeInSeconds: 0
	},
	[RUNNING_TYPES.REST]: {
		timeInSeconds: 0
	},
	active: true,
	useIntroTimer: false
}

let runningState = {
	stateType: RUNNING_TYPES.ACTIVE,
	currentTick: RUNNING_TYPE_CONFIGURATIONS.ACTIVE.defaultTimeInSeconds
}

let workoutStep = 0;

//listeners
container.addEventListener("click", stepThroughWorkout);

//functions
function setConfigurationState(newState = {}) {
	configurationState = {
		...configurationState,
		...newState
	}
}

function setRunningState(newState = {}) {
	runningState = {
		...runningState,
		...newState
	}
}

function updateActiveTime(time) {
	setConfigurationState({ [RUNNING_TYPES.ACTIVE]: { timeInSeconds: parseInt(time, 10) } });
}

function updateRestTime(time) {
	setConfigurationState({ [RUNNING_TYPES.REST]: { timeInSeconds: parseInt(time, 10) } });
}

function stepThroughWorkout() {
    clearInterval(runningInterval);

    //check for end of workout
    if (workoutStep == workoutJson.list.length) {
        workoutStep = 0;
        endWorkout();
        return;
    }

    document.body.style.backgroundColor = RUNNING_TYPE_CONFIGURATIONS.ACTIVE.backgroundColor;

    config = workoutJson.list[workoutStep];
    exerciseName.innerText = config.name;
    if (workoutJson.list[workoutStep].type == "time") {
        //start timer
        updateActiveTime(config.amount);
        updateRestTime(config.rest);
        counterDiv.innerText = config.amount;

        toggleTimerState();
    } else {
        counterDiv.innerText = config.amount;
    }
    workoutStep += 1;
}

function toggleTimerState() {
    console.log(configurationState);
    setRunningState({
        stateType: RUNNING_TYPES.ACTIVE,
        currentTick: configurationState.ACTIVE.timeInSeconds
    });
    runningInterval = setInterval(startTimer, 1000);
}

function endWorkout() {
    noSleep.disable();
    clearInterval(runningInterval);
    showStartScreen();
}

function startTimer() {
	//check state
	let currentRunningTypeConfiguration = RUNNING_TYPE_CONFIGURATIONS[runningState.stateType];
	//if running is down to 0 change to next state types
	if (runningState.currentTick <= 0) {
		beep();
		let nextRunningStateType = currentRunningTypeConfiguration.nextType;
		setRunningState({
			stateType: nextRunningStateType,
			currentTick: configurationState[nextRunningStateType].timeInSeconds
		});
		currentRunningTypeConfiguration = RUNNING_TYPE_CONFIGURATIONS[runningState.stateType];
	}
	//set background
	document.body.style.backgroundColor = currentRunningTypeConfiguration.backgroundColor;
	//draw number
	counterDiv.innerText = runningState.currentTick;
	//minus 1 from the current tick
	setRunningState({
		currentTick: runningState.currentTick - 1
	});
}

function beep() {
	var snd = new Audio("data:audio/wav;base64,");
	snd.play();
}

function showStartScreen() {
	document.body.style.backgroundColor = "#fff";
	counterDiv.innerText = "Tap to start!";
    exerciseName.innerText = "";
}

function showStarting() {
	document.body.style.backgroundColor = "#fff";
	counterDiv.innerText = "Starting!";
    exerciseName.innerText = "";
}
